import React, { useState } from 'react';

import './ExpenseForm.css';

const ExpenseForm = () => {



    const titleChangeFunction = (event) => {
        // console.log("title changed!");
        // console.log(event.target.value);



    }

    const amountChangeFunction = () => {
        console.log("amount changed!");
    }

    const dateChangeFunction = () => {
        console.log("date changed!");
    }




    return (
        <form>
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <label>Title</label>
                    <input onChange = {titleChangeFunction} type='text' />
                </div>
                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input onChange ={amountChangeFunction} type='number' min='0.01' step='0.01' />
                </div>
                <div className='new-expense__control'>
                    <label>Date</label>
                    <input onChange = {dateChangeFunction} type='date' min='2019-01-01' max='2022-12-31' />
                </div>
            </div>
            <div className='new-expense__actions'>
                <button type='submit'>Add Expense</button>
            </div>
        </form>

    );

}

export default ExpenseForm;
